import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import {
  Card,
  Button,
  CardActions,
  CardContent,
  CardHeader,
  Typography,
  Grid,
  TextField,
} from "@material-ui/core";
import { useNavigate } from "react-router-dom";
import { getDifferentiation } from "../../functions";

const useStyles = makeStyles((theme) => ({
    cardHeader: {
      backgroundColor:
        theme.palette.type === "light"
          ? theme.palette.grey[200]
          : theme.palette.grey[700],
    },

    card: {
      marginLeft: theme.spacing(10),
      marginRight: theme.spacing(10),
      marginTop: theme.spacing(10),
    },
  }));

export default function Page() {

    const classes = useStyles()
    const navigate = useNavigate();
  const [h, seth] = useState(0.64);
  const [p, setp] = useState(2);

  const [result, setresult] = useState("");

  const back = () => {
    navigate("/WilawanCommath/main");
  };

  const generate = () => {
    const data = {
      h:h,
      p:p

    };
    getDifferentiation(data).then((res) => {
       setresult(res)
    });
  };

    return (
      <div>
         <Typography align="center">
      <Card className={classes.card}>
        <CardHeader
          title="Chapter 4"
          titleTypographyProps={{ align: "center" }}
          subheaderTypographyProps={{ align: "center" }}
          className={classes.cardHeader}
        />
        <CardContent>
          <Grid container spacing={3} alignItems="flex-end">
            <Grid item xs={12} sm={6} md={6}>
              <TextField
                id="outlined-basic"
                value={h}
                label="H value"
                variant="outlined"
                onChange={(e) => seth(e.target.value)}
                color="secondary"
              />
            </Grid>
            <Grid item xs={12} sm={6} md={6}>
              <TextField
                id="outlined-basic"
                value={p}
                label="P value"
                variant="outlined"
                onChange={(e) => setp(e.target.value)}
                color="secondary"
              />
            </Grid>
            <Grid item xs={12} sm={12} md={12}>
                <TextField
                  id="outlined-read-only-input"
                  label="result"
                    value={result===""?"":result}
                  InputProps={{
                    readOnly: true,
                  }}
                  variant="outlined"
                  color="secondary"
                />
            </Grid>
          </Grid>
        </CardContent>
        <CardActions>
          <Grid container spacing={5} alignItems="flex-end">
            <Grid item xs={12} sm={6} md={6}>
              <Button
                onClick={generate}
                fullWidth
                variant="contained"
                color="secondary"
              >
                calculate
              </Button>
            </Grid>
            <Grid item xs={12} sm={6} md={6}>
              <Button
                onClick={back}
                fullWidth
                variant="outlined"
                color="secondary"
              >
                back
              </Button>
            </Grid>
          </Grid>
        </CardActions>
      </Card>
    </Typography>

    <br></br>
    <br></br>


    <center><h9>"Finding the Central Difference Approximations: CDA using Richardson Extrapolation."</h9>
                    </center>  
                    <br></br>
                    <br></br>
                    <br></br>

    </div>
    )
}
